% Programa filtros fspecial()% Luis Angel Alvarado Hernandez% Vision Computacional% Prof pkg load imageimgMuestra = imread("image.jpeg");filter = fspecial("average");imgFiltrada = imfilter(imgMuestra, filter);figure(1);imshow(imgFiltrada);filter = fspecial("disk");imgFiltrada = imfilter(imgMuestra, filter);figure(2);imshow(imgFiltrada);filter = fspecial("gaussian");imgFiltrada = imfilter(imgMuestra, filter);figure(3);imshow(imgFiltrada);filter = fspecial("log");imgFiltrada = imfilter(imgMuestra, filter);figure(4);imshow(imgFiltrada);filter = fspecial("laplacian", 0.5);imgFiltrada = imfilter(imgMuestra, filter);figure(5);imshow(imgFiltrada);%imwrite(imgFiltrada,"lap.jpg");filter = fspecial("unsharp", .5);imgFiltrada = imfilter(imgMuestra, filter);figure(6);imshow(imgFiltrada);%imwrite(imgFiltrada,"unsh.jpg");filter = fspecial("motion");imgFiltrada = imfilter(imgMuestra, filter);figure(7);imshow(imgFiltrada);%imwrite(imgFiltrada,"mot.jpg");filter = fspecial("sobel");imgFiltrada = imfilter(imgMuestra, filter);figure(8);imshow(imgFiltrada);%imwrite(imgFiltrada,"sobel.jpg");filter = fspecial("prewitt");imgFiltrada = imfilter(imgMuestra, filter);figure(9);imshow(imgFiltrada);%imwrite(imgFiltrada,"pret.jpg");filter = fspecial("kirsch");imgFiltrada = imfilter(imgMuestra, filter);figure(10);imshow(imgFiltrada);%imwrite(imgFiltrada,"kir.jpg");figure(11);imshow(imgMuestra);