  for i=1:8    my_img=imread(num2str((i-1)', '../img/with-mask/0%d.jpeg')); % available in MATLAB, not sure octave  kx= [1 ,0 ,-1; 2,0,-2; 1, 0 ,-1];  ky= [1,2,1; 0,0, 0; -1, -2 ,-1];  figure;  H = conv2(im2double(my_img),kx,'same');  V = conv2(im2double(my_img),ky,'same');  E = sqrt(H.*H + V.*V); % or sqrt(H.^2+V.^2)  subplot(2, 1, 1);    img2 = imshow(my_img, []);  %my_img_bin = my_img>=150;  subplot(2, 1, 2);  imshow(E, []);  [centers, radii] = imfindcircles(E, [30 40]);   viscircles (centers, radii);   title ("found circles in red");endfor